components/beautyfeatures/navigation-lis:
{{#if children}}
<a class="navPages-action-toggle" href="#"
    data-collapsible="navPages-{{id}}{{idSuffix}}"
    {{#if menu_type '!=' 'vertical'}}data-collapsible-disabled-breakpoint="medium"{{/if}}
    data-collapsible-disabled-state="open"
    data-collapsible-enabled-state="closed">
    <span class="_more">More <svg class="icon"><use xlink:href="#icon-add"></use></svg></span>
    <span class="_less">Less <svg class="icon"><use xlink:href="#icon-remove"></use></svg></span>
</a>
<a class="navPages-action has-subMenu {{#if menu_type '==' 'vertical'}}navPages-action--vertical{{/if}}" href="{{url}}"
    {{#if menu_type '!=' 'vertical'}}data-instantload{{/if}}
    {{#if id '==' category.id}}data-current-category{{/if}}
>{{name}}</a>
<div class="navPage-subMenu
        {{#if menu_type '==' 'vertical'}}
            navPage-subMenu--vertical
        {{else if menu_type '==' 'standard'}}
            navPage-subMenu--standard
        {{/if}}
    " id="navPages-{{id}}{{idSuffix}}">
    {{#if theme_settings.navPages_banners}}
        {{#if menu_type '==' 'mega'}}
            <div class="navPage-subMenu-rightBanners" data-emthemesmodez-remote-banner="emthemesModezBannersBlockMegaMenuRight{{@index}}"></div>
        {{/if}}
    {{/if}}
    <ul class="navPage-subMenu-list">
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a class="navPages-action-toggle" href="#"
                        data-collapsible="navPages-{{id}}{{../../idSuffix}}"
                        {{#if ../../menu_type '!=' 'vertical'}}data-collapsible-disabled-breakpoint="medium"{{/if}}
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        <span class="_more">More <svg class="icon"><use xlink:href="#icon-add"></use></svg></span>
                        <span class="_less">Less <svg class="icon"><use xlink:href="#icon-remove"></use></svg></span>
                    </a>
                    <a
                        class="navPage-subMenu-action has-subMenu"
                        href="{{url}}"
                        {{#if ../../menu_type '!=' 'vertical'}}data-instantload{{/if}}
                        {{#if id '==' ../../category.id}}data-current-category{{/if}}
                    >{{name}}</a>
                    <ul class="navPage-childList" id="navPages-{{id}}{{../../idSuffix}}">
                        {{#each children}}
                        <li class="navPage-childList-item">
                            {{> components/beautyfeatures/navigation-list-child 
                                    category_id=../../../category.id 
                                    idSuffix=../../../idSuffix 
                                    theme_settings=../../../theme_settings
                                    menu_type=../../../menu_type}}
                        </li>
                        {{/each}}
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action" href="{{url}}" data-instantload>{{lang 'common.view_all'}}</a>
                        </li>
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action has-subMenu" href="{{url}}" data-instantload {{#if id '==' ../../category.id}}data-current-category{{/if}}>{{name}}</a>
                    <ul class="navPage-childList" id="navPages-{{id}}{{../../idSuffix}}">
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action" href="{{url}}" data-instantload>{{lang 'common.view_all'}}</a>
                        </li>
                    </ul>
                {{/if}}
            </li>
        {{/each}}
    </ul>
    {{#if theme_settings.navPages_banners}}
        {{#if menu_type '==' 'mega'}}
            <div class="navPage-subMenu-bottomBanners" data-emthemesmodez-remote-banner="emthemesModezBannersBlockMegaMenuBottom{{@index}}"></div>
        {{/if}}
    {{/if}}
</div>
{{else}}
<a class="navPages-action" href="{{url}}" data-instantload {{#if id '==' category.id}}data-current-category{{/if}}>{{name}}</a>
{{/if}}

